public var kCurrentVersion: String {
    return (Bundle.main.infoDictionary?["CFBundleShortVersionString"] as? String) ?? "-"
}

public var kBuildVersion: String {
    return (Bundle.main.infoDictionary?["CFBundleVersion"] as? String) ?? "-"
}

// MARK: 全局参数
let kDefaultErrorMessage: String = "Server error"

/*唯一识别码 UDID*/
public var kGlobalUDID : String {
    get{
        var UDIDStr = UIDevice.current.identifierForVendor?.uuidString
        if UDIDStr == ""  {
            UDIDStr = " "
        }
        return UDIDStr ?? ""
    }
}

/** 设备号*/
public var kGlobalDevice : String {
    get{
        let DeviceStr = "\(UIDevice.current.model):iOS\(UIDevice.current.systemVersion)"
        return DeviceStr
    }
}

// MARK: 随机数生成
func randomCustom(min: Int, max: Int) -> Int {
    return Int(arc4random()) % (max - min + 1) + min
}

